let startTime = Date.now();

function apply3DEffect() {
  const timeElapsed = Date.now() - startTime;
  const elements = document.querySelectorAll('.hero, .features, .contact, .contact-form');
  
  elements.forEach(element => {
    const rotation = (timeElapsed / 1000) * 10; // Increase rotation over time
    const scale = 1 + (timeElapsed / 100000); // Gradually increase the scale

    element.style.transform = `rotateY(${rotation}deg) scale(${scale})`;
  });

  requestAnimationFrame(apply3DEffect);
}

document.addEventListener('DOMContentLoaded', () => {
  requestAnimationFrame(apply3DEffect);
});
