let startTime = Date.now();

function applyTextEffect() {
  const timeElapsed = Date.now() - startTime;
  const elements = document.querySelectorAll('.hero, .features, .contact, .contact-form');

  elements.forEach(element => {
    const scale = 1 + (timeElapsed / 300000); // Gradually increase the scale over time

    element.style.transform = `scale(${scale})`;
  });

  requestAnimationFrame(applyTextEffect);
}

document.addEventListener('DOMContentLoaded', () => {
  requestAnimationFrame(applyTextEffect);
});
